var assert = require('assert');
var nodeunit = require('nodeunit');
var logger;

exports.instantiation = function(test) {
	test.doesNotThrow(function () {
		logger = require('../index.js');
	});
	
	test.done();
};

exports.logging = function(test) {
	test.doesNotThrow(function () {
		logger.debug('Debug message');
		logger.info('Info message');
		logger.warn('Warning message');
		logger.error('Error message');
	});
	
	test.done();
};

exports.iologging = function(test) {
	test.doesNotThrow(function () {
		logger.debug('Writing out message', 'io', ['Other Computer', 'w', '>']);
		logger.debug('Reading in message', 'io', ['Other Computer', 'r', '<']);
		
		logger.info('Writing out message', 'io', ['Other Computer', 'w', '>']);
		logger.info('Reading in message', 'io', ['Other Computer', 'r', '<']);
		
		logger.warn('Writing out message', 'io', ['Other Computer', 'w', '>']);
		logger.warn('Reading in message', 'io', ['Other Computer', 'r', '<']);
		
		logger.error('Writing out message', 'io', ['Other Computer', 'w', '>']);
		logger.error('Reading in message', 'io', ['Other Computer', 'r', '<']);
	});
	
	test.done();
};